import React, { Component } from 'react'
import "./style.css"
import axios from 'axios'
import {default as App} from './Counter'
// import banner from "./img/placesBanner.jpeg"

// import {Card, Button} from 'react-bootstrap'

const Places = (props) => {


    return (

        <div className='blog-post__container '>

            <div className="blog-post filterDiv" >
                <div className="blog-post__img">
                <img  src={props.amuse.imageURL} alt="Card cap" />
                </div>
                <div className="blog-post__info">
                    <h5 className="blog-post__title">{props.amuse.name}</h5>
                    <p className="blog-post__text">{props.amuse.description}</p>
                    
                </div>
                <App />
            </div>
        </div>

    )
}
export default class AmusesList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            amuses: []
        }
    }


    componentDidMount() {
        axios.get("http://localhost:5000/amusements/")
            .then(response => {
                this.setState({ amuses: response.data })
            })
            .catch((error) => {
                console.log(error)
            })
    }


    AmusesList() {
        return this.state.amuses.map(currentamuse => {
            return <Places amuse={currentamuse} key={currentamuse._id} />
        })
    }

    render() {
        return (
            <div className="container">
                <style>

                </style>
                <>
  <div
    style={{
      position: "relative",
      width: "100vw",
      height: 0,
      paddingTop: "33.3333%",
      paddingBottom: 48,
      boxShadow: "0 2px 8px 0 rgba(63,69,81,0.16)",
        
      overflow: "hidden",
      borderRadius: 8,
      willChange: "transform"
    }}
  >
    <iframe
      loading="lazy"
      style={{
        position: "absolute",
        width: "100vw",
        height: "100%",
        top: 0,
        left: 0,
        border: "none",
        padding: 0,
        margin: 0
      }}
      src="https://www.canva.com/design/DAFDrbvtBBs/view?embed"
      allowFullScreen="allowfullscreen"
      allow="fullscreen"
    ></iframe>
  </div>
  <a
    href="https://www.canva.com/design/DAFDrbvtBBs/view?utm_content=DAFDrbvtBBs&utm_campaign=designshare&utm_medium=embeds&utm_source=link"
    target="_blank"
    rel="noopener"
  >
    Places
  </a>{" "}
  by Habil Tabesh
</>


                <div className="d-flex flex-wrap">
                    {this.AmusesList()}
                </div>
            </div>
        )
    }
}
