import React, {Component} from "react";
import "./style.css"
import axios from 'axios'
import {Card, Button} from 'react-bootstrap'



const Event = (props) =>{
    return(
    <div className="container">

        <Card style={{ width: '18rem' }}>
        <Card.Img variant="top" src={props.event.imageURL} />
  <Card.Body>
    <Card.Title>{props.event.name}</Card.Title>
    <Card.Text>
     {props.event.description}
     {props.event.price}
    </Card.Text>
    <Button className='center' variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>
    </div>
    )
 }

export default class EventsList extends Component{
constructor(props){
    super(props)
    // this.deleteEvent = this.deleteEvent.bind(this)
   
    this.state = {
        events: []
    }
}


componentDidMount() {
    axios.get("http://localhost:5000/events/")
    .then(response => {
        this.setState({ events: response.data})
    })
    .catch((error)=>{
        console.log(error)
    })
}

// deleteEvent(id){
//     axios.delete(BACKEND_URL + 'event/' +id)
//     .then(res => console.log(res.data))

//     this.setState({
//         event: this.state.event.filter(el => el._id !== id)
//     })
// }
EventsList(){
    return this.state.events.map(currentevent => {
        return <Event event={currentevent}  key={currentevent._id}/>
    })
}

render() {
    return(
    <div className="container"> 
        <h4 className="center">Events</h4>
        <h5 className="center">Come join the party.</h5>
        {this.EventsList()}
    </div>
    )
}
}
