import React, { Component } from "react";
import "./style.spence.css"
import axios from 'axios'
import { BACKEND_URL } from "./config";
import {Card, Button} from 'react-bootstrap'
import '../../'


const Events = props =>(
    
    <div className="container">
        <h4 className="center">Events</h4>
        <h5>Come join the party.</h5>
        <Card style={{ width: '18rem' }}>
        <Card.Img variant="top" src={props.events.imageURL} />
  <Card.Body>
    <Card.Title>{props.events.name}</Card.Title>
    <Card.Text>
     {props.events.description}
     {props.events.price}
    </Card.Text>
    <Button className='center' variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>
    </div>
    
)

export default class EventsList extends Component{
constructor(props){
    super(props)
    this.deleteEvent = this.deleteComment.bind(this)
   
    this.state = {comments: []}
}


componentDidMount() {
    axios.get("http://localhost:3000/comments")
    .then(response => {
        this.setState({ comments: response.data})
    })
    .catch((error)=>{
        console.log(error)
    })
}

deleteComment(id){
    axios.delete(BACKEND_URL + 'comment/' +id)
    .then(res => consle.log(res.data))

    this.setState({
        comment: this.state.comments.filter(el => el._id !== id)
    })
}
EventsList(){
    return this.state.comments.map(currentcomment => {
        return <Events comment={currentcomment} deleteComment={this.deleteComment} key={currentcomment._id}/>
    })
}

render() {
    return(
        <div className="container">
        <h4 className="center">Events</h4>
        <h5>Come join the party.</h5>
        <Card style={{ width: '18rem' }}>
        <Card.Img variant="top" src=""/>
  <Card.Body>
    <Card.Title></Card.Title>
    <Card.Text>
    </Card.Text>
    <Button className='center' variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>
    </div>
    )
}
}
