import React, {Component} from "react";
import "./style.css"
import axios from 'axios'
import {Card, Button} from 'react-bootstrap'



const Event = (props) =>{
    return(
<div className="card" style="width: 18rem;">
  <img className="card-img-top" src={props.event.imageURL} alt="Card image cap"/>
  <div className="card-body">
    <h5 className="card-title">Card title</h5>
    <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
    <a href="#" className="btn btn-primary">Go somewhere</a>
  </div>
</div>
    )
 }

export default class EventsList extends Component{
constructor(props){
    super(props)
    // this.deleteEvent = this.deleteEvent.bind(this)
   
    this.state = {
        events: []
    }
}


componentDidMount() {
    axios.get("http://localhost:5000/events/")
    .then(response => {
        this.setState({ events: response.data})
    })
    .catch((error)=>{
        console.log(error)
    })
}

// deleteEvent(id){
//     axios.delete(BACKEND_URL + 'event/' +id)
//     .then(res => console.log(res.data))

//     this.setState({
//         event: this.state.event.filter(el => el._id !== id)
//     })
// }
EventsList(){
    return this.state.events.map(currentevent => {
        return <Event event={currentevent}  key={currentevent._id}/>
    })
}

render() {
    return(
    <div className="container"> 
        <h4 className="center">Events</h4>
        <h5 className="center">Come join the party.</h5>
        <div className="d-flex flex-wrap">
        {this.EventsList()}
        </div>
    </div>
    )
}
}
